package diagramas.de.flujo;

public class Main2 {
	// Imprimir en pantalla los 10 primeros numeros impares
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 1;
		for (n = 1; n <= 20; n++) {
			if (n % 2 == 1) {
				System.out.println(n);
			}
		}

	}

}
